D = (I + reflect(B,C)*(I))/2;
E = (I + reflect(C,A)*(I))/2;
F = (I + reflect(A,B)*(I))/2;

draw(A--B--C--cycle);
draw(incircle(A,B,C));

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
