Q = (A + reflect(B,C)*(A))/2;

R = (A + reflect(D,E)*(A))/2;

draw((2*R - E)--D--C--(2*Q - B));

draw(A--P);

draw(A--Q);

draw(A--R);

draw(B--A--E);

draw((O--B),dashed);

