label(rotate(30)*"$4^{\prime\prime}$",.5*(2dir(210)+2dir(210)+4dir(-60))+(0,-.5),W);
label(rotate(-30)*"$6^{\prime\prime}$",.5*(2dir(150)+2dir(150)+6dir(60))+(1,1),W);
