draw(Circle((-1/2,0),1/2));
draw(Circle((-3/2,0),1/2));
draw((-2.2,0)--(2.2,0));
draw((0,-1/2)--(0,1/2));

label("$\dots$", (2.2,0.2));
label("$\dots$", (-2.2,0.2));

dot("$(-\frac{3}{2},0)$", (-3/2,0), S);
