draw((0,-1.2)--(0,1.2),p=black+1.2bp,Arrows(0.15cm));
draw((-1.2,0)--(1.2,0),p=black+1.2bp,Arrows(0.15cm));
A = (1,0);
O= (0,0);
label("$x$",(1.2,0),SE);
label("$y$",(0,1.2),NE);

P = rotate(210)*A;
D = foot(P,A,-A);
draw(O--P--D);
draw(rightanglemark(O,D,P,2));
draw(Circle(O,1));
label("$O$",O,SE);
label("$P$",P,SW);
//label("$A$",A,SE);
